mapper.update:
UPDATE DASH_DATASET
USER_ID = #{userId}, APP_ID = #{appId}, FOLDER_ID = #{folderId}, DATASOURCE_ID = #{datasourceId}, CATA_NAME = #{cataName}, NAME = #{name}, CONTENT = #{content}, CREATE_TIME = #{createTime}, LAST_TIME = #{lastTime}, DESCRIPTION = #{description}
WHERE DATASET_ID = #{id}

mapper.delete:
DELETE FROM DASH_DATASET
WHERE DATASET_ID IN

    #{id}

mapper.select:
SELECT count(1)
FROM DASH_DATASET
WHERE dataset_id = #{arg1}
and (#{arg0} = '${admin_user_id}' OR user_id = #{arg0}
    OR dataset_id IN (
    SELECT res_id FROM DASH_ADMIN_USER_ROLE ur
    LEFT JOIN DASH_ADMIN_ROLE_RES rr ON ur.role_id = rr.role_id
    WHERE ur.user_id = #{arg0} AND rr.res_type = 'dataset' AND rr.permission LIKE #{arg2}
    )
)
