mapper.select:
SELECT B.*
FROM DASH_WIDGET B
WHERE WIDGET_ID = #{value}

mapper.update:
UPDATE DASH_WIDGET
USER_ID = #{userId}, APP_ID = #{appId}, FOLDER_ID = #{folderId}, DATASOURCE_ID = #{datasourceId}, DATASET_ID = #{datasetId}, NAME = #{name}, CATA_NAME = #{cataName}, CONTENT = #{content}, CREATE_TIME = #{createTime}, LAST_TIME = #{lastTime}, DESCRIPTION = #{description}
WHERE WIDGET_ID = #{id}

mapper.delete:
DELETE FROM DASH_WIDGET
WHERE WIDGET_ID IN

    #{id}

mapper.select:
SELECT count(1) FROM DASH_WIDGET WHERE widget_id = #{arg1}
and (#{arg0} = '${admin_user_id}' OR user_id = #{arg0} OR widget_id IN
    (SELECT res_id FROM DASH_ADMIN_USER_ROLE ur
    LEFT JOIN DASH_ADMIN_ROLE_RES rr ON ur.role_id = rr.role_id
    WHERE ur.user_id = #{arg0} AND rr.res_type = 'widget' AND rr.permission LIKE #{arg2}
    )
)
